.switch {
	position: relative;
	display: block;
	width: 50px;
	height: 26px;
    margin-right: 5px;
}

.switch input {
	opacity: 0;
	width: 0;
	height: 0;
}

.slider {
	position: absolute;
	cursor: pointer;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background-color: white;
	-webkit-transition: .4s;
	transition: .4s;
    border: 2px solid #064663;
}

.slider:before {
	position: absolute;
	content: "";
	height: 19px;
	width: 19px;
	left: 2px;
	bottom: 1.5px;
	background-color: #064663;
	-webkit-transition: .4s;
	transition: .4s;
}

input:checked+.slider {
	background-color: white;
}

input:focus+.slider {
	box-shadow: 0 0 1px #064663;
}

input:checked+.slider:before {
	-webkit-transform: translateX(24px);
	-ms-transform: translateX(24px);
	transform: translateX(24px);
}

/* Rounded sliders */
.slider.round {
	border-radius: 34px;
}

.slider.round:before {
	border-radius: 50%;
}

