@import "../../../../assets/scss/variables";

.sidebar {
	top: 0;
    left: 0;
	z-index: 20;
	position: fixed;
	height: 100%;
	background-color: $blue-dark;
	color: $white;
	width: 300px;
	padding: 1rem;
	transform: translateX(-250px);
	transition: 0.3s;
	overflow-x: hidden;

	img.logo {
		display: block;
		margin: 10px auto 0;
		height: 50px;
	}

	.sidebar-footer {
		display: block;
		text-align: center;
		font-size: 0.7rem;
		color: $gray-400;
	}

	h3 {
		opacity: 0;
		padding: 0;
		font-size: 0.9rem;
	}

	.sidebar-sections {
		transform: translateX(230px);
		height: 80%;
		margin: 25px 0;
		transition: 0.3s transform;
		display: flex;
		flex-direction: column;
		overflow-y: auto;
	}

	.hamburger-menu {
		position: absolute;
		right: 10px;
		top: 10px;
		height: 36px;
		width: 36px;
		transition: 0.3s;
		cursor: pointer;

		div {
			background-color: $white;
			width: 100%;
			height: 3px;
			margin-top: 7px;
			transition: 0.3s;

			&#second-hambar {
				display: block;
				transition: 0.3s;
			}
		}
	}

	&.opened {
		transform: translateX(0px);

		.hamburger-menu > div {
			background-color: $white;
			width: 100%;
			height: 3px;
			transition: 0.3s;

			&#first-hambar {
				transform: rotate(45deg) translateY(7px) translateX(7px);
			}

			&#second-hambar {
				opacity: 0;
				transition: 0.5s opacity;
			}

			&#third-hambar {
				transform: rotate(-45deg) translateY(-7px) translateX(7px);
			}
		}

		h3 {
			opacity: 1;
		}

		.sidebar-sections {
			transform: translateX(0px);
			width: 300px;
			align-content: center;
			padding-left: 0;

			sh-navbar-section {
				transform: translateX(0px);

				sh-navbar-link {
					width: 100%;

					.navbar-link-text {
						display: inline-block;
					}

					&:hover {
						> div::before {
							width: calc(100% + 15px);
						}
					}
				}
			}
		}
	}
}

